INSERT INTO faqs (category, question, answer, order_num, active, created_at, updated_at) VALUES
                                                                                             ('GENERAL', '마이데이터 테스트베드란 무엇인가요?', '마이데이터 테스트베드는 금융분야 마이데이터 서비스 개발자를 위한 API 테스트 환경입니다. 실제 서비스 출시 전 API 연동 및 기능을 검증할 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('GENERAL', '테스트베드 이용 비용이 있나요?', '마이데이터 테스트베드는 무료로 제공됩니다. 회원가입 후 바로 이용하실 수 있습니다.', 2, true, NOW(), NOW()),
                                                                                             ('SIGNUP', '회원가입은 어떻게 하나요?', '상단 메뉴의 "회원가입" 버튼을 클릭하여 약관동의 → 휴대폰인증 → 회원정보입력 → 이메일인증 단계를 완료하시면 됩니다.', 1, true, NOW(), NOW()),
                                                                                             ('SIGNUP', '이메일 인증 메일이 오지 않아요.', '스팸 메일함을 확인해 주세요. 그래도 메일이 없다면 회원가입을 다시 시도하시거나 문의하기를 통해 연락 주시기 바랍니다.', 2, true, NOW(), NOW()),
                                                                                             ('API', 'API 규격 문서는 어디서 확인할 수 있나요?', 'API 가이드 메뉴에서 데이터 표준 API 기본규격, 인증규격, 각종 API 규격 문서를 확인하실 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('API', 'API 호출 시 인증은 어떻게 하나요?', 'OAuth 2.0 기반의 인증 방식을 사용합니다. API 가이드의 인증규격 문서를 참고해 주세요.', 2, true, NOW(), NOW()),
                                                                                             ('TEST', '테스트 데이터는 어떻게 생성하나요?', '테스트베드 메뉴에서 테스트용 가상 데이터를 생성하고 API 연동 테스트를 진행할 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('CONFORMANCE', '적합성 심사란 무엇인가요?', '마이데이터 서비스 출시 전 API 규격 준수 여부를 검증하는 절차입니다. 기능적합성 심사와 보안취약점 점검으로 구성됩니다.', 1, true, NOW(), NOW());
INSERT INTO resources (title, description, file_path, file_name, file_size, download_count, author_id, created_at, updated_at) VALUES
('마이데이터 서비스 가이드라인 v2.1', '마이데이터 서비스 개발을 위한 종합 가이드라인입니다. API 연동, 보안 요구사항, 테스트 절차 등을 포함합니다.', '/uploads/guide_v2.1.pdf', '마이데이터_서비스_가이드라인_v2.1.pdf', 5242880, 156, NULL, '2025-01-20 10:00:00', '2025-01-20 10:00:00'),
('API 규격 문서 (정보제공자용)', '정보제공자를 위한 API 규격 상세 문서입니다. 엔드포인트 명세, 요청/응답 형식 등을 설명합니다.', '/uploads/api_spec_provider.pdf', 'API_규격_정보제공자용.pdf', 3145728, 89, NULL, '2025-01-18 14:30:00', '2025-01-18 14:30:00'),
('마이데이터 테스트베드 사용 매뉴얼', '테스트베드 이용 방법을 설명하는 사용자 매뉴얼입니다. 처음 사용하시는 분들께 권장드립니다.', '/uploads/testbed_manual.pdf', '테스트베드_사용_매뉴얼.pdf', 2097152, 234, NULL, '2025-01-15 09:00:00', '2025-01-15 09:00:00'),
('보안 점검 체크리스트', '마이데이터 서비스 보안 점검을 위한 체크리스트입니다. 출시 전 필수 점검 항목을 포함합니다.', '/uploads/security_checklist.xlsx', '보안_점검_체크리스트.xlsx', 524288, 67, NULL, '2025-01-10 11:20:00', '2025-01-10 11:20:00'),
('샘플 테스트 데이터', '테스트에 사용할 수 있는 샘플 데이터 모음입니다. JSON, XML 형식의 예제를 포함합니다.', '/uploads/sample_data.zip', '샘플_테스트_데이터.zip', 10485760, 45, NULL, '2025-01-05 16:45:00', '2025-01-05 16:45:00'),
('OAuth 2.0 연동 가이드', '마이데이터 인증을 위한 OAuth 2.0 연동 방법을 상세히 설명합니다.', '/uploads/oauth_guide.pdf', 'OAuth_2.0_연동_가이드.pdf', 1572864, 112, NULL, '2025-01-03 13:15:00', '2025-01-03 13:15:00'),
('에러 코드 레퍼런스', 'API 응답에서 발생할 수 있는 에러 코드와 해결 방법을 정리한 문서입니다.', '/uploads/error_codes.pdf', '에러_코드_레퍼런스.pdf', 819200, 78, NULL, '2024-12-28 09:30:00', '2024-12-28 09:30:00');
