INSERT INTO faqs (category, question, answer, order_num, active, created_at, updated_at) VALUES
                                                                                             ('GENERAL', '마이데이터 테스트베드란 무엇인가요?', '마이데이터 테스트베드는 금융분야 마이데이터 서비스 개발자를 위한 API 테스트 환경입니다. 실제 서비스 출시 전 API 연동 및 기능을 검증할 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('GENERAL', '테스트베드 이용 비용이 있나요?', '마이데이터 테스트베드는 무료로 제공됩니다. 회원가입 후 바로 이용하실 수 있습니다.', 2, true, NOW(), NOW()),
                                                                                             ('SIGNUP', '회원가입은 어떻게 하나요?', '상단 메뉴의 "회원가입" 버튼을 클릭하여 약관동의 → 휴대폰인증 → 회원정보입력 → 이메일인증 단계를 완료하시면 됩니다.', 1, true, NOW(), NOW()),
                                                                                             ('SIGNUP', '이메일 인증 메일이 오지 않아요.', '스팸 메일함을 확인해 주세요. 그래도 메일이 없다면 회원가입을 다시 시도하시거나 문의하기를 통해 연락 주시기 바랍니다.', 2, true, NOW(), NOW()),
                                                                                             ('API', 'API 규격 문서는 어디서 확인할 수 있나요?', 'API 가이드 메뉴에서 데이터 표준 API 기본규격, 인증규격, 각종 API 규격 문서를 확인하실 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('API', 'API 호출 시 인증은 어떻게 하나요?', 'OAuth 2.0 기반의 인증 방식을 사용합니다. API 가이드의 인증규격 문서를 참고해 주세요.', 2, true, NOW(), NOW()),
                                                                                             ('TEST', '테스트 데이터는 어떻게 생성하나요?', '테스트베드 메뉴에서 테스트용 가상 데이터를 생성하고 API 연동 테스트를 진행할 수 있습니다.', 1, true, NOW(), NOW()),
                                                                                             ('CONFORMANCE', '적합성 심사란 무엇인가요?', '마이데이터 서비스 출시 전 API 규격 준수 여부를 검증하는 절차입니다. 기능적합성 심사와 보안취약점 점검으로 구성됩니다.', 1, true, NOW(), NOW());